import React, { useState } from 'react';
import axios from 'axios'; // Import axios to send HTTP requests

const ObjectsQuiz = ({ user }) => {
  const levels = ['easy', 'medium', 'hard'];

  const objects = {
    easy: {
      image: 'data:image/jpeg;base64,', // URL to the apple image
      question: 'Guess the fruit!',
      correctAnswer: 'apple',
      options: ['mango', 'apple', 'banana'],
    },
    medium: {
      image: 'data:image/jpeg;base64,', // URL to the vehicle image
      question: 'What vehicle is this?',
      correctAnswer: 'bus',
      options: ['car', 'bus', 'bicycle', 'train'],
    },
    hard: {
      image: 'data:image/jpeg;base64,', // URL to the instrument image
      question: 'Name this musical instrument!',
      correctAnswer: 'violin',
      options: ['guitar', 'flute', 'violin', 'drums', 'piano'],
    },
  };
  

  const [currentLevelIndex, setCurrentLevelIndex] = useState(0);
  const [selected, setSelected] = useState('');
  const [isCorrect, setIsCorrect] = useState(null);
  const [attempts, setAttempts] = useState(0);
  const [totalScore, setTotalScore] = useState(0);
  const [gameCompleted, setGameCompleted] = useState(false);

  const level = levels[currentLevelIndex];
  const object = objects[level];

  const handleSelect = (option) => {
    const correct = option === object.correctAnswer;
    setSelected(option);
    setIsCorrect(correct);
    setAttempts((prev) => prev + 1);

    if (correct) {
      const currentScore = attempts === 0 ? 100 : attempts === 1 ? 75 : 50;
      setTotalScore((prev) => prev + currentScore);

      setTimeout(() => {
        if (currentLevelIndex < levels.length - 1) {
          setCurrentLevelIndex((prev) => prev + 1);
          resetLevelState();
        } else {
          setGameCompleted(true);
          saveScoreToBackend(totalScore + currentScore);
        }
      }, 1500);
    }
  };

  const saveScoreToBackend = async (finalScore) => {
    try {
      const response = await axios.post('http://localhost:5000/api/users/score', {
        name: user.name,
        score: finalScore,
        game: 'objects',
      });
      console.log('Score saved successfully:', response.data);
    } catch (error) {
      console.error('Error saving score:', error.response?.data || error.message);
    }
  };

  const renderImage = () => {
    return <img src={object.image} alt="quiz object" className="quiz-image" />;
  };

  const resetLevelState = () => {
    setSelected('');
    setIsCorrect(null);
    setAttempts(0);
  };

  return (
    <div className="objects-game">
      <h3 className="level-indicator">
        Level {currentLevelIndex + 1}: {level.toUpperCase()}
      </h3>
      {user && <p className="player-name">Player: {user.wardName}</p>}

      <div className="object-container">
        <h2 className="question-text">{object.question}</h2>
        <div className="object-image">{renderImage()}</div>
      </div>

      <div className="answer-options">
        {object.options.map((option) => (
          <button
            key={option}
            className={`answer-button ${selected === option ? (isCorrect ? 'correct' : 'incorrect') : ''}`}
            onClick={() => handleSelect(option)}
          >
            {option}
          </button>
        ))}
      </div>

      {isCorrect !== null && (
        <div className={`feedback ${isCorrect ? 'correct-feedback' : 'incorrect-feedback'}`}>
          {isCorrect
            ? `Great job${user.wardName ? ', ' + user.wardName : ''}! That's correct!`
            : 'Try again!'}

          {isCorrect && currentLevelIndex < levels.length - 1 && (
            <div className="level-completion">
              <p>Moving to next level...</p>
              <div className="loading-dots"></div>
            </div>
          )}

          {gameCompleted && (
            <div className="game-completion">
              <p>🎉 Congratulations! You've completed all levels!</p>
              <p>Total Score: {totalScore}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ObjectsQuiz;
